public class ExampleSpringServiceTest {

    private static class TestClass {
        public void methodWithString(String param) {}
        public void methodWithList(List<String> param) {}
        public void methodWithMap(Map<String, Integer> param) {}
        public void methodWithNestedGeneric(List<Map<String, Integer>> param) {}
        public void methodWithMultipleParams(String param1, List<Integer> param2) {}
