    private static class ArrayTestClass {
        public void methodWithArray(String[] param) {}
    }

    @Test
    public void testResolveParameterType_SimpleType() throws NoSuchMethodException {
        Method method = TestClass.class.getMethod("methodWithString", String.class);
        MethodParameter methodParameter = new MethodParameter(method, 0);
        ExampleSpringService service = new ExampleSpringService();
        ResolvableType resolvableType = service.resolveParameterType(methodParameter);
        assertEquals(String.class, resolvableType.getRawClass());
    }

    @Test
    public void testResolveParameterType_GenericList() throws NoSuchMethodException {
        Method method = TestClass.class.getMethod("methodWithList", List.class);
        MethodParameter methodParameter = new MethodParameter(method, 0);
        ExampleSpringService service = new ExampleSpringService();
        ResolvableType resolvableType = service.resolveParameterType(methodParameter);

        assertEquals(List.class, resolvableType.getRawClass());
        ResolvableType genericType = resolvableType.getGeneric(0);
        assertNotNull(genericType);
        assertEquals(String.class, genericType.resolve());
    }

    @Test
    public void testResolveParameterType_GenericMap() throws NoSuchMethodException {
        Method method = TestClass.class.getMethod("methodWithMap", Map.class);
        MethodParameter methodParameter = new MethodParameter(method, 0);
        ExampleSpringService service = new ExampleSpringService();
        ResolvableType resolvableType = service.resolveParameterType(methodParameter);

        assertEquals(Map.class, resolvableType.getRawClass());
        ResolvableType keyType = resolvableType.getGeneric(0);
        ResolvableType valueType = resolvableType.getGeneric(1);
        assertNotNull(keyType);
        assertNotNull(valueType);
        assertEquals(String.class, keyType.resolve());
        assertEquals(Integer.class, valueType.resolve());
    }

    @Test
    public void testResolveParameterType_NestedGeneric() throws NoSuchMethodException {
        Method method = TestClass.class.getMethod("methodWithNestedGeneric", List.class);
        MethodParameter methodParameter = new MethodParameter(method, 0);
        ExampleSpringService service = new ExampleSpringService();
        ResolvableType resolvableType = service.resolveParameterType(methodParameter);

        assertEquals(List.class, resolvableType.getRawClass());
        ResolvableType innerMapType = resolvableType.getGeneric(0);
        assertNotNull(innerMapType);
        assertEquals(Map.class, innerMapType.getRawClass());

        ResolvableType keyType = innerMapType.getGeneric(0);
        ResolvableType valueType = innerMapType.getGeneric(1);
        assertNotNull(keyType);
        assertNotNull(valueType);
        assertEquals(String.class, keyType.resolve());
        assertEquals(Integer.class, valueType.resolve());
    }

