import java.lang.reflect.Method;
import java.util.Collection;

public class ExampleSpringService {
    public void handleClientSubscriptions(Object subscriptionManager) {
        try {
            Class<?> serverStateClass = Class.forName("org.springframework.messaging.simp.broker.ServerState");
            Object runningState = Enum.valueOf((Class<Enum>) serverStateClass, "RUNNING");
            Method getServerState = subscriptionManager.getClass().getMethod("getServerState");
            Object serverState = getServerState.invoke(subscriptionManager);
            if (serverState == null || !serverState.equals(runningState)) {
                return;
            }
            Method getActiveClients = subscriptionManager.getClass().getMethod("getActiveClients");
            Collection<?> clientIds = (Collection<?>) getActiveClients.invoke(subscriptionManager);
            Method getSubscriptions = subscriptionManager.getClass().getMethod("getSubscriptions", String.class);
            Method trackSubscription = subscriptionManager.getClass().getMethod("trackSubscription", String.class, String.class);
            clientIds.parallelStream().forEach(clientIdObj -> {
                try {
                    String clientId = clientIdObj.toString();
                    Collection<?> subscriptions = (Collection<?>) getSubscriptions.invoke(subscriptionManager, clientId);
                    if (subscriptions != null) {
                        subscriptions.parallelStream().forEach(subscriptionObj -> {
                            try {
                                String subscription = subscriptionObj.toString();
                                trackSubscription.invoke(subscriptionManager, clientId, subscription);
                            } catch (Exception ignore) {}
                        });
                    }
                } catch (Exception ignore) {}
            });
        } catch (Exception ignore) {}
    }
}

