import org.springframework.beans.factory.BeanFactory;
import org.springframework.beans.factory.ListableBeanFactory;
import org.springframework.beans.factory.BeanFactoryUtils;
import org.springframework.context.ApplicationContext;
import org.springframework.context.ApplicationContextAware;
import org.springframework.context.ApplicationListener;
import org.springframework.context.event.AbstractApplicationEventMulticaster;
import org.springframework.context.event.ApplicationEventMulticaster;
import org.springframework.stereotype.Service;

@Service
public class ExampleSpringService implements ApplicationContextAware {

    private ApplicationContext applicationContext;
    private AbstractApplicationEventMulticaster multicaster;

    @Override
    public void setApplicationContext(ApplicationContext applicationContext) {
        this.applicationContext = applicationContext;
        ApplicationEventMulticaster mc = applicationContext.getBean(ApplicationEventMulticaster.class);
        if (mc instanceof AbstractApplicationEventMulticaster) {
            this.multicaster = (AbstractApplicationEventMulticaster) mc;
        } else {
            throw new IllegalStateException("ApplicationEventMulticaster is not an instance of AbstractApplicationEventMulticaster");
        }
    }

    public void registerApplicationListener(ApplicationListener<?> listener) {
        BeanFactory beanFactory = applicationContext.getAutowireCapableBeanFactory();
        if (!(beanFactory instanceof ListableBeanFactory)) {
            throw new IllegalStateException("BeanFactory is not a ListableBeanFactory");
        }
        ListableBeanFactory listableBeanFactory = (ListableBeanFactory) beanFactory;
        String[] beanNames = BeanFactoryUtils.beanNamesForTypeIncludingAncestors(listableBeanFactory, listener.getClass());
        if (beanNames.length == 0) {
            throw new IllegalArgumentException("No bean found for listener of type: " + listener.getClass());
        }
        String listenerBeanName = beanNames[0];
        multicaster.addApplicationListenerBean(listenerBeanName);
    }
}

