import static org.junit.jupiter.api.Assertions.assertEquals;

import org.springframework.stereotype.Component;
import org.springframework.beans.factory.annotation.Autowired;

import java.lang.annotation.Retention;
import java.lang.annotation.RetentionPolicy;
import java.lang.annotation.Target;
import java.lang.annotation.ElementType;

public class ExampleSpringServiceTest {

    private final ExampleSpringService service = new ExampleSpringService();

    @Test
    public void testGenerateAnnotatedMethodsLog_NoAnnotatedMethods() {
        String log = service.generateAnnotatedMethodsLog(NoAnnotatedMethodsComponent.class);
        assertEquals("", log);
    }

    @Test
    public void testGenerateAnnotatedMethodsLog_SomeAnnotatedMethods() {
        String log = service.generateAnnotatedMethodsLog(SomeAnnotatedMethodsComponent.class);
        String expected = 
            "public void ExampleSpringServiceTest$SomeAnnotatedMethodsComponent.annotatedMethodOne()\n" +
            "public java.lang.String ExampleSpringServiceTest$SomeAnnotatedMethodsComponent.annotatedMethodTwo(java.lang.String)\n";
        assertEquals(expected, log);
    }

    @Test
    public void testGenerateAnnotatedMethodsLog_AllAnnotatedMethods() {
        String log = service.generateAnnotatedMethodsLog(AllAnnotatedMethodsComponent.class);
        String expected = 
            "public void ExampleSpringServiceTest$AllAnnotatedMethodsComponent.methodOne()\n" +
            "public void ExampleSpringServiceTest$AllAnnotatedMethodsComponent.methodTwo()\n" +
            "public void ExampleSpringServiceTest$AllAnnotatedMethodsComponent.methodThree()\n";
        assertEquals(expected, log);
    }

    @Component
    static class NoAnnotatedMethodsComponent {
        public void methodOne() {}
        public void methodTwo() {}
    }

    @Component
    static class SomeAnnotatedMethodsComponent {
        @Autowired
        public void annotatedMethodOne() {}

        @GetMapping("/test")
        public String annotatedMethodTwo(String param) {
            return param;
        }

        public void nonAnnotatedMethod() {}
    }

    @Component
    static class AllAnnotatedMethodsComponent {
        @PostMapping("/create")
        public void methodOne() {}

        @Autowired
        public void methodTwo() {}

        @GetMapping("/read")
        public void methodThree() {}
    }

    // Define custom GetMapping annotation
    @Retention(RetentionPolicy.RUNTIME)
    @Target(ElementType.METHOD)
    public @interface GetMapping {
        String value() default "";
    }

    // Define custom PostMapping annotation
    @Retention(RetentionPolicy.RUNTIME)
    @Target(ElementType.METHOD)
    public @interface PostMapping {
        String value() default "";
    }
}

