import org.springframework.scheduling.concurrent.ThreadPoolTaskScheduler;
import java.util.Date;
import java.util.concurrent.Delayed;
import java.util.concurrent.ScheduledFuture;
import java.util.concurrent.TimeUnit;

public class ExampleSpringService {

    private final ThreadPoolTaskScheduler taskScheduler;

    public ExampleSpringService() {
        taskScheduler = new ThreadPoolTaskScheduler();
        taskScheduler.setPoolSize(10);
        taskScheduler.initialize();
    }

    public ScheduledFuture<?> scheduleWithPriority(Runnable task, long delay, int priority) {
        PrioritizedTask prioritizedTask = new PrioritizedTask(task, priority, System.currentTimeMillis() + delay);
        return taskScheduler.schedule(prioritizedTask, new Date(prioritizedTask.getStartTime()));
    }

    private static class PrioritizedTask implements Runnable, Delayed {
        private final Runnable task;
        private final int priority;
        private final long startTime;

        public PrioritizedTask(Runnable task, int priority, long startTime) {
            this.task = task;
            this.priority = priority;
            this.startTime = startTime;
        }

        public long getStartTime() {
            return startTime;
        }

        @Override
        public void run() {
            task.run();
        }

        @Override
        public long getDelay(TimeUnit unit) {
            long delay = startTime - System.currentTimeMillis();
            return unit.convert(delay, TimeUnit.MILLISECONDS);
        }

        @Override
        public int compareTo(Delayed o) {
            if (this == o)
                return 0;
            if (o instanceof PrioritizedTask) {
                PrioritizedTask other = (PrioritizedTask) o;
                return Integer.compare(other.priority, this.priority);
            }
            return 0;
        }
    }
}

