import java.util.concurrent.ScheduledFuture;
import org.springframework.scheduling.concurrent.ThreadPoolTaskScheduler;
import org.springframework.scheduling.Trigger;
import java.time.Instant;
import java.util.concurrent.atomic.AtomicLong;

public class ExampleSpringService {
    private final ThreadPoolTaskScheduler taskScheduler = new ThreadPoolTaskScheduler();
    private final AtomicLong sequencer = new AtomicLong();

    {
        taskScheduler.initialize();
    }

    public ScheduledFuture<?> scheduleWithPriority(Runnable task, long delay, int priority) {
        class PriorityTask extends ThreadPoolTaskScheduler implements Runnable {
            final Runnable delegate;
            final int priority;
            final long scheduledTime;
            final long sequence;

            PriorityTask(Runnable delegate, int priority, long scheduledTime) {
                this.delegate = delegate;
                this.priority = priority;
                this.scheduledTime = scheduledTime;
                this.sequence = sequencer.getAndIncrement();
            }

            @Override
            public void run() {
                delegate.run();
            }

            @Override
            public int compareTo(Object o) {
                if (o == this) return 0;
                if (!(o instanceof PriorityTask)) return 0;
                PriorityTask other = (PriorityTask) o;
                int prioCmp = Integer.compare(other.priority, this.priority);
                if (prioCmp != 0) return prioCmp;
                int timeCmp = Long.compare(this.scheduledTime, other.scheduledTime);
                if (timeCmp != 0) return timeCmp;
                return Long.compare(this.sequence, other.sequence);
            }
        }

        PriorityTask priorityTask = new PriorityTask(task, priority, System.currentTimeMillis() + delay);
        Trigger trigger = context -> Instant.ofEpochMilli(System.currentTimeMillis() + delay);
        return taskScheduler.schedule(priorityTask, trigger);
    }
}

