import java.util.concurrent.CompletableFuture;
import java.util.function.Supplier;
import org.springframework.cache.concurrent.ConcurrentMapCache;

public class ExampleSpringService {
    private final ConcurrentMapCache cache = new ConcurrentMapCache("asyncCache");

    public <T> CompletableFuture<T> fetchAsync(Object key, Supplier<CompletableFuture<T>> valueProvider) {
        Object cached = cache.retrieve(key, () -> {
            CompletableFuture<T> future = valueProvider.get();
            return future.thenApply(value -> value).toCompletableFuture();
        });
        if (cached instanceof CompletableFuture) {
            @SuppressWarnings("unchecked")
            CompletableFuture<T> futureValue = (CompletableFuture<T>) cached;
            return futureValue;
        }
        return CompletableFuture.completedFuture(null);
    }
}

