    public void testStreamGroupedEntities_singleGroup() {
        // Arrange
        MultiValueMap<String, Entity> groupedMap = new LinkedMultiValueMap<>();
        Entity entity1 = new Entity();
        entity1.setName("entity1");
        Entity entity2 = new Entity();
        entity2.setName("entity2");
        List<Entity> group = List.of(entity1, entity2);
        groupedMap.put("group1", group);
        ExampleSpringService service = new ExampleSpringService(groupedMap);

        // Act
        Spliterator<List<Entity>> spliterator = service.streamGroupedEntities();

        // Assert
        assertNotNull(spliterator, "Spliterator should not be null");
        assertTrue(spliterator.tryAdvance(list -> {
            assertEquals(2, list.size(), "Group should contain 2 entities");
            assertEquals("entity1", list.get(0).getName(), "First entity name should match");
            assertEquals("entity2", list.get(1).getName(), "Second entity name should match");
        }), "Spliterator should have one group");
        assertFalse(spliterator.tryAdvance(list -> fail("There should only be one group")), "Spliterator should have only one group");
    }

    @Test
    public void testStreamGroupedEntities_multipleGroups() {
        // Arrange
        MultiValueMap<String, Entity> groupedMap = new LinkedMultiValueMap<>();
        Entity entity1 = new Entity();
        entity1.setName("entity1");
