        entity3.setName("entity3");
        List<Entity> group1 = List.of(entity1, entity2);
        List<Entity> group2 = List.of(entity3);
        groupedMap.put("group1", group1);
        groupedMap.put("group2", group2);
        ExampleSpringService service = new ExampleSpringService(groupedMap);

        // Act
        Spliterator<List<Entity>> spliterator = service.streamGroupedEntities();

        // Assert
        assertNotNull(spliterator, "Spliterator should not be null");
        List<List<Entity>> groups = new ArrayList<>();
        spliterator.forEachRemaining(groups::add);
        assertEquals(2, groups.size(), "There should be 2 groups");
        assertTrue(groups.contains(group1), "Groups should contain group1");
        assertTrue(groups.contains(group2), "Groups should contain group2");
