import java.util.concurrent.CompletableFuture;
import org.springframework.scheduling.annotation.AsyncResult;
import org.springframework.util.concurrent.ListenableFuture;
import org.springframework.util.concurrent.ListenableFutureCallback;

public class ExampleSpringService {
    public <V> CompletableFuture<V> handleAsyncException(Throwable ex) {
        ListenableFuture<V> listenableFuture = AsyncResult.forExecutionException(ex);
        CompletableFuture<V> completableFuture = new CompletableFuture<>();

        listenableFuture.addCallback(new ListenableFutureCallback<V>() {
            @Override
            public void onSuccess(V result) {
                completableFuture.complete(result);
            }

            @Override
            public void onFailure(Throwable throwable) {
                completableFuture.completeExceptionally(throwable);
            }
        });

        return completableFuture;
    }
}

