    public void testBroadcastMessage_IncrementsUniqueId() {
        // Arrange
        String content = "Hello, World!";
        int initialId = service.uniqueIdGenerator.get();

        // Act
        service.broadcastMessage(content, 0);
        int firstId = service.uniqueIdGenerator.get();

        service.broadcastMessage(content, 0);
        int secondId = service.uniqueIdGenerator.get();

        // Assert
        assertEquals(initialId + 1, firstId, "Unique ID should increment by 1 after first broadcast");
        assertEquals(firstId + 1, secondId, "Unique ID should increment by 1 after second broadcast");
    }

