    public void testBroadcastMessage_ThreadSafety() throws InterruptedException {
        // Arrange
        int threadCount = 100;
        ExecutorService executor = Executors.newFixedThreadPool(threadCount);
        CountDownLatch latch = new CountDownLatch(threadCount);

        // Act
        for (int i = 0; i < threadCount; i++) {
            executor.execute(() -> {
                service.broadcastMessage("Concurrent Message", 0);
                latch.countDown();
            });
