import org.springframework.aop.framework.AdvisedSupport;

public class ExampleSpringService {
    private final AdvisedSupport advisedSupport;

    public ExampleSpringService(AdvisedSupport advisedSupport) {
        this.advisedSupport = advisedSupport;
    }

    public void updateProxyInterfaces(Class<?>... interfaces) {
        if (interfaces == null) {
            interfaces = new Class<?>[0];
        }
        Class<?>[] current = advisedSupport.getProxiedInterfaces();
        for (Class<?> intf : current) {
            boolean keep = false;
            for (Class<?> i : interfaces) {
                if (intf == i) {
                    keep = true;
                    break;
                }
            }
            if (!keep) {
                advisedSupport.removeInterface(intf);
            }
        }
        for (Class<?> intf : interfaces) {
            if (!advisedSupport.isInterfaceProxied(intf)) {
                advisedSupport.addInterface(intf);
            }
        }
    }
}

