import org.springframework.beans.factory.config.BeanDefinition;
import org.springframework.beans.factory.config.BeanDefinitionHolder;
import org.springframework.beans.factory.xml.BeanDefinitionParserDelegate;
import org.springframework.beans.factory.xml.ParserContext;
import org.w3c.dom.Element;
import org.w3c.dom.Node;
import org.w3c.dom.NodeList;

public class ExampleSpringService {

    public BeanDefinitionHolder parseCustomBeanElement(Element element, BeanDefinitionParserDelegate delegate) {
        BeanDefinitionHolder holder = delegate.parseBeanDefinitionElement(element);
        if (holder != null) {
            NodeList childNodes = element.getChildNodes();
            for (int i = 0; i < childNodes.getLength(); i++) {
                Node node = childNodes.item(i);
                if (node instanceof Element childElement && !delegate.isDefaultNamespace(childElement)) {
                    delegate.parseCustomElement(childElement);
                }
            }
            delegate.getReaderContext().getRegistry().registerBeanDefinition(holder.getBeanName(), holder.getBeanDefinition());
        }
        return holder;
    }
}

