import org.springframework.beans.factory.support.AbstractBeanDefinitionReader;
import org.springframework.beans.factory.support.BeanDefinitionRegistry;
import org.springframework.beans.factory.support.DefaultListableBeanFactory;
import org.springframework.beans.factory.xml.XmlBeanDefinitionReader;
import org.springframework.core.io.Resource;
import org.springframework.core.io.ResourceLoader;
import org.springframework.core.io.support.PathMatchingResourcePatternResolver;

public class ExampleSpringService {

    public void initializeBeanDefinitions(ResourceLoader resourceLoader) {
        BeanDefinitionRegistry registry = new DefaultListableBeanFactory();
        AbstractBeanDefinitionReader reader = new XmlBeanDefinitionReader(registry);
        reader.setResourceLoader(resourceLoader);

        PathMatchingResourcePatternResolver resolver = new PathMatchingResourcePatternResolver(resourceLoader);
        String[] patterns = {"classpath*:beans/*.xml", "file:/config/beans-*.xml"};

        try {
            for (String pattern : patterns) {
                Resource[] resources = resolver.getResources(pattern);
                reader.loadBeanDefinitions(resources);
            }
        } catch (Exception e) {
            throw new RuntimeException("Failed to load bean definitions", e);
        }
    }
}

