import org.springframework.stereotype.Component;

@Component
public class ExampleSpringService implements InstantiationAwareBeanPostProcessor {

    @Override
    public PropertyValues postProcessProperties(PropertyValues pvs, Object bean, String beanName) {
        return customizeBeanProperties(pvs, bean, beanName);
    }

    public PropertyValues customizeBeanProperties(PropertyValues pvs, Object bean, String beanName) {
        MutablePropertyValues mutablePvs = new MutablePropertyValues(pvs);
        // Example custom logic: Modify a property named "exampleProperty"
        if (mutablePvs.contains("exampleProperty")) {
            mutablePvs.add("exampleProperty", "newValue");
        }
        return mutablePvs;
    }
}

