import java.util.Collection;
import java.util.HashMap;
import java.util.Map;

import org.springframework.beans.factory.NoUniqueBeanDefinitionException;
import org.springframework.beans.factory.support.DefaultListableBeanFactory;

public class ExampleSpringService extends DefaultListableBeanFactory {

    protected String selectTopPriorityBean(Collection<Object> candidates, Class<?> requiredType) {
        Map<String, Object> candidateMap = new HashMap<>();
        int index = 0;
        for (Object candidate : candidates) {
            String beanName = "candidateBean" + index++;
            candidateMap.put(beanName, candidate);
        }
        return determineHighestPriorityCandidate(candidateMap, requiredType);
    }

}

