import org.springframework.core.io.buffer.DefaultDataBuffer;
import org.springframework.core.io.buffer.DefaultDataBufferFactory;
import org.springframework.stereotype.Service;

@Service
public class ExampleSpringService {

    private final DefaultDataBufferFactory dataBufferFactory;
    private DefaultDataBuffer buffer;

    public ExampleSpringService() {
        this.dataBufferFactory = new DefaultDataBufferFactory();
        this.buffer = dataBufferFactory.allocateBuffer();
    }

    public DefaultDataBuffer transferDataSegment(byte[] destination, int offset, int length) {
        if (destination == null) {
            throw new IllegalArgumentException("Destination byte array must not be null");
        }
        int available = buffer.readableByteCount();
        int toRead = Math.min(length, available);
        buffer.read(destination, offset, toRead);
        return buffer;
    }
}

