import java.util.function.Function;

public class ExampleSpringService {

    private final Cache cache;

    public ExampleSpringService(Cache cache) {
        this.cache = cache;
    }

    public ValueWrapper computeIfAbsent(Object key, Function<? super Object, ? extends Object> mappingFunction) {
        ValueWrapper existing = cache.get(key);
        if (existing != null) {
            return existing;
        }
        Object value = mappingFunction.apply(key);
        return cache.putIfAbsent(key, value);
    }
}

