import java.lang.reflect.AnnotatedElement;
import java.util.HashMap;
import java.util.Map;
import java.util.List;

import org.springframework.core.annotation.AnnotatedElementUtils;
import org.springframework.util.MultiValueMap;

public class ExampleSpringService {
    public Map<String, Object> retrieveMergedAnnotationAttributes(AnnotatedElement element, String annotationName) {
        MultiValueMap<String, Object> allAttributes = AnnotatedElementUtils.getAllAnnotationAttributes(
                element, annotationName, true, true);
        if (allAttributes == null) {
            return new HashMap<>();
        }
        Map<String, Object> mergedAttributes = new HashMap<>();
        for (Map.Entry<String, List<Object>> entry : allAttributes.entrySet()) {
            List<Object> values = entry.getValue();
            if (!values.isEmpty()) {
                mergedAttributes.put(entry.getKey(), values.get(values.size() - 1));
            }
        }
        return mergedAttributes;
    }
}

