import java.lang.annotation.Annotation;
import java.util.Optional;

import org.springframework.beans.factory.NoSuchBeanDefinitionException;
import org.springframework.beans.factory.config.ConfigurableListableBeanFactory;
import org.springframework.stereotype.Service;

@Service
public class ExampleSpringService {

    private final ConfigurableListableBeanFactory beanFactory;

    public ExampleSpringService(ConfigurableListableBeanFactory beanFactory) {
        this.beanFactory = beanFactory;
    }

    public <A extends Annotation> Optional<A> retrieveBeanAnnotation(String beanName, Class<A> annotationType) {
        try {
            A annotation = beanFactory.findAnnotationOnBean(beanName, annotationType);
            return Optional.ofNullable(annotation);
        } catch (NoSuchBeanDefinitionException e) {
