import org.springframework.beans.factory.BeanCreationException;
import org.springframework.beans.factory.support.DefaultListableBeanFactory;
import org.springframework.context.ApplicationContext;
import org.springframework.context.ConfigurableApplicationContext;
import org.springframework.stereotype.Service;

import java.util.Map;

@Service
public class ExampleSpringService {

    private final DefaultListableBeanFactory beanFactory;

    public ExampleSpringService(ApplicationContext applicationContext) {
        if (applicationContext instanceof ConfigurableApplicationContext) {
            this.beanFactory = (DefaultListableBeanFactory) ((ConfigurableApplicationContext) applicationContext).getBeanFactory();
        } else {
            throw new IllegalArgumentException("ApplicationContext must be ConfigurableApplicationContext");
        }
    }

    public <T> Map<String, T> collectAllHandlers(Class<T> handlerType, boolean includePrototypeHandlers, boolean initializeImmediately) {
        Map<String, T> handlers = beanFactory.getBeansOfType(handlerType, includePrototypeHandlers, initializeImmediately);

        if (initializeImmediately && includePrototypeHandlers) {
            handlers.keySet().forEach(beanName -> {
                if (!beanFactory.isSingleton(beanName)) {
                    try {
                        beanFactory.getBean(beanName);
                    } catch (BeanCreationException e) {
                        throw e;
                    }
                }
            });
        }

        return handlers;
    }
}

