public class ExampleSpringService {
    private final Object bean;
    private final Validator validator;

    public ExampleSpringService(Object bean, Validator validator) {
        this.bean = bean;
        this.validator = validator;
    }

    public void configureBeanProperties(PropertyValues pvs, boolean ignoreUnknown, boolean validateProperties) {
        BeanWrapper beanWrapper = new BeanWrapperImpl(bean);
        try {
            beanWrapper.setPropertyValues(pvs, ignoreUnknown);
            if (validateProperties && validator != null) {
                Errors errors = new BeanPropertyBindingResult(bean, bean.getClass().getName());
                validator.validate(bean, errors);
                if (errors.hasErrors()) {
                    throw new IllegalArgumentException("Validation failed: " + errors.getAllErrors());
                }
            }
        } catch (BeansException e) {
            throw e;
        }
    }
}

