import org.springframework.context.support.AbstractMessageSource;
import org.springframework.stereotype.Component;
import java.text.MessageFormat;
import java.util.Locale;
import java.util.Map;
import java.util.concurrent.ConcurrentHashMap;

@Component
public class ExampleSpringService extends AbstractMessageSource {

    private final Map<String, Map<Locale, String>> messages = new ConcurrentHashMap<>();

    public ExampleSpringService() {
        // Initialize messages, in a real scenario load from properties or other sources
        addMessage("welcome", Locale.ENGLISH, "Welcome, {0}!");
        addMessage("welcome", Locale.FRENCH, "Bienvenue, {0}!");
    }

    public void addMessage(String code, Locale locale, String message) {
        messages.computeIfAbsent(code, k -> new ConcurrentHashMap<>()).put(locale, message);
    }

    @Override
    protected MessageFormat resolveCode(String code, Locale locale) {
        String msg = getMessageInternal(code, locale);
        if (msg != null) {
            return new MessageFormat(msg, locale);
        }
        return null;
    }

    private String getMessageInternal(String code, Locale locale) {
        Map<Locale, String> localeMap = messages.get(code);
        if (localeMap != null) {
            return localeMap.get(locale);
        }
        return null;
    }

    public String getLocalizedMessage(String code, Object[] args, Locale locale) {
        String msg = getMessageInternal(code, locale);
        if (msg == null) {
            msg = code;
        }
        return formatMessage(msg, args, locale);
    }
}

