    public void setBeanFactory(BeanFactory beanFactory) throws BeansException {
        if (beanFactory instanceof ConfigurableListableBeanFactory) {
            this.beanFactory = (ConfigurableListableBeanFactory) beanFactory;
        } else {
            throw new IllegalArgumentException("BeanFactory must be ConfigurableListableBeanFactory");
        }
    }

    @Override
    public Object postProcessBeforeInitialization(Object bean, String beanName) throws BeansException {
        return injectImportMetadata(bean, beanName);
    }

    public Object injectImportMetadata(Object bean, String beanName) {
        if (bean instanceof ImportAware) {
            BeanDefinition beanDefinition = this.beanFactory.getBeanDefinition(beanName);
            Object source = beanDefinition.getSource();
            if (source instanceof AnnotationMetadata) {
                ((ImportAware) bean).setImportMetadata((AnnotationMetadata) source);
            }
        }
        return bean;
    }
}

