import org.springframework.core.Conventions;
import org.springframework.core.GenericTypeResolver;
import org.springframework.core.MethodParameter;
import org.springframework.lang.Nullable;

import java.lang.reflect.Method;

public class ExampleSpringService {

    public String generateDynamicIdentifier(MethodParameter parameter, @Nullable Object value) {
        return resolveName(parameter, value);
    }

    private String resolveName(MethodParameter parameter, @Nullable Object value) {
        String objectName = null;
        if (parameter.getParameterIndex() != -1) {
            objectName = parameter.getParameterName();
        } else {
            try {
                Method method = parameter.getMethod();
                if (method != null) {
                    Class<?> containingClass = parameter.getContainingClass();
                    Class<?> resolvedType = GenericTypeResolver.resolveReturnType(method, containingClass);
                    objectName = Conventions.getVariableNameForReturnType(method, resolvedType, value);
                }
            } catch (IllegalArgumentException ex) {
                // insufficient type information
            }
        }
        if (objectName == null) {
            int index = parameter.getParameterIndex();
            objectName = parameter.getExecutable().getName() + (index != -1 ? ".arg" + index : ".returnValue");
        }
        return objectName;
    }
}

