import org.springframework.util.Assert;

public class ExampleSpringService extends BaseService {

    private static final String PARAM_QUALITY_FACTOR = "qualityFactor";

    protected void validateQualityFactor(String qualityFactor) {
        checkParameters(PARAM_QUALITY_FACTOR, qualityFactor);
    }
}

class BaseService {
    protected void checkParameters(String parameter, String value) {
        if ("qualityFactor".equals(parameter)) {
            value = unquote(value);
            double d = Double.parseDouble(value);
            Assert.isTrue(d >= 0D && d <= 1D,
                "Invalid quality value \"" + value + "\": should be between 0.0 and 1.0");
        }
    }

    protected String unquote(String value) {
        if (value != null && value.startsWith("\"") && value.endsWith("\"")) {
            return value.substring(1, value.length() - 1);
        }
        return value;
    }
}

