    public static class Result {
        public static final Result TRUE = new Result(true, null);
        public static final Result FALSE = new Result(false, null);

        private final boolean value;
        private final Consumer<Map<String, Object>> modifyAttributes;

        private Result(boolean value, Consumer<Map<String, Object>> modifyAttributes) {
            this.value = value;
            this.modifyAttributes = modifyAttributes;
        }

        public static Result of(boolean value, @Nullable Consumer<Map<String, Object>> modifyAttributes) {
            if (modifyAttributes == null) {
                return value ? TRUE : FALSE;
            } else {
                return new Result(value, modifyAttributes);
            }
        }

        public boolean isValue() {
            return value;
        }

        public Consumer<Map<String, Object>> getModifyAttributes() {
            return modifyAttributes;
        }
    }
}

