import java.util.ArrayList;
import java.util.List;
import java.util.function.BiFunction;

public class ExampleSpringService {
    private List<PathComponent> pathComponents;

    public ExampleSpringService(List<PathComponent> pathComponents) {
        this.pathComponents = pathComponents;
    }

    public PathComponent buildEncodedPathComponent(BiFunction<String, Type, String> encoder) {
        List<PathComponent> encodedComponents = new ArrayList<>(this.pathComponents.size());
        for (PathComponent pathComponent : this.pathComponents) {
            encodedComponents.add(pathComponent.encode(encoder));
        }
        return new PathComponentComposite(encodedComponents);
    }

    public static class PathComponent {
        private String value;
        private Type type;

        public PathComponent(String value, Type type) {
            this.value = value;
            this.type = type;
        }

        public PathComponent encode(BiFunction<String, Type, String> encoder) {
            String encodedValue = encoder.apply(this.value, this.type);
            return new PathComponent(encodedValue, this.type);
        }

        public String getValue() {
            return value;
        }

        public Type getType() {
            return type;
        }
    }

    public static class PathComponentComposite extends PathComponent {
        private List<PathComponent> components;

        public PathComponentComposite(List<PathComponent> components) {
            super("", null);
            this.components = components;
        }

        @Override
        public String getValue() {
            StringBuilder sb = new StringBuilder();
            for (PathComponent component : components) {
                sb.append("/").append(component.getValue());
            }
            return sb.toString();
        }

        public List<PathComponent> getComponents() {
            return components;
        }
    }

    public enum Type {
        ALPHA,
        NUMERIC,
        ALPHANUMERIC,
        SPECIAL
    }
}

